	(350, 1, '2024-01-12 13:16:20', '2024-01-12 13:16:20', '', 'image-10', '', 'inherit', 'open', 'closed', '', 'image-10', '', '', '2024-01-12 13:16:20', '2024-01-12 13:16:20', '', 348, 'http://wordpress_project.test/wp-content/uploads/2024/01/image-10.png', 0, 'attachment', 'image/png', 0),
	(351, 1, '2024-01-12 13:16:36', '2024-01-12 13:16:36', '', 'image-11', '', 'inherit', 'open', 'closed', '', 'image-11', '', '', '2024-01-12 13:16:36', '2024-01-12 13:16:36', '', 348, 'http://wordpress_project.test/wp-content/uploads/2024/01/image-11.png', 0, 'attachment', 'image/png', 0),
